operation.add:
	if ($this->user->hasPermission('access', 'catalog/customer_import')){
	$tool[] = array(
		'name'	   => 'Customer Import Tool',
		'href'     => $this->url->link('catalog/customer_import', 'token=' . $this->session->data['token'], true),
		'children' => array()		
	);
}

if ($this->user->hasPermission('access', 'catalog/customer_export')) {
	$tool[] = array(
		'name'	   => 'Customer Export Tool',
		'href'     => $this->url->link('catalog/customer_export', 'token=' . $this->session->data['token'], true),
		'children' => array()	
	);
}
